import React, { useState, useEffect, useCallback } from 'react';
import { Calendar, Clock, Plus, Edit3, Trash2, Search, Filter, ChevronLeft, ChevronRight, X, Save, AlertCircle } from 'lucide-react';

const EventCalendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [showEventForm, setShowEventForm] = useState(false);
  const [editingEvent, setEditingEvent] = useState(null);
  const [selectedDate, setSelectedDate] = useState(null);
  const [draggedEvent, setDraggedEvent] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [conflicts, setConflicts] = useState([]);

  const categories = [
    { value: 'work', label: 'Work', color: 'bg-blue-500' },
    { value: 'personal', label: 'Personal', color: 'bg-green-500' },
    { value: 'health', label: 'Health', color: 'bg-red-500' },
    { value: 'social', label: 'Social', color: 'bg-purple-500' },
    { value: 'other', label: 'Other', color: 'bg-gray-500' }
  ];

  const [eventForm, setEventForm] = useState({
    title: '',
    date: '',
    time: '',
    description: '',
    category: 'personal',
    recurrence: 'none',
    customRecurrence: 1,
    weeklyDays: [],
    endDate: ''
  });

  // Load events from localStorage on component mount
  useEffect(() => {
    const savedEvents = localStorage.getItem('calendar-events');
    if (savedEvents) {
      setEvents(JSON.parse(savedEvents));
    }
  }, []);

  // Save events to localStorage whenever events change
  useEffect(() => {
    localStorage.setItem('calendar-events', JSON.stringify(events));
    checkConflicts();
  }, [events]);

  const checkConflicts = useCallback(() => {
    const conflictList = [];
    const sortedEvents = [...events].sort((a, b) => new Date(a.date + ' ' + a.time) - new Date(b.date + ' ' + b.time));
    
    for (let i = 0; i < sortedEvents.length - 1; i++) {
      for (let j = i + 1; j < sortedEvents.length; j++) {
        const event1 = sortedEvents[i];
        const event2 = sortedEvents[j];
        
        if (event1.date === event2.date && event1.time === event2.time) {
          conflictList.push({ event1: event1.id, event2: event2.id });
        }
      }
    }
    setConflicts(conflictList);
  }, [events]);

  const generateRecurringEvents = (baseEvent, startDate, endDate) => {
    const recurringEvents = [];
    const start = new Date(startDate);
    const end = endDate ? new Date(endDate) : new Date(start.getFullYear() + 1, start.getMonth(), start.getDate());
    
    let current = new Date(start);
    
    while (current <= end) {
      if (current > start) {
        recurringEvents.push({
          ...baseEvent,
          id: `${baseEvent.id}-${current.toISOString().split('T')[0]}`,
          date: current.toISOString().split('T')[0],
          isRecurring: true,
          parentId: baseEvent.id
        });
      }
      
      switch (baseEvent.recurrence) {
        case 'daily':
          current.setDate(current.getDate() + 1);
          break;
        case 'weekly':
          if (baseEvent.weeklyDays.length > 0) {
            let nextDay = new Date(current);
            nextDay.setDate(nextDay.getDate() + 1);
            while (!baseEvent.weeklyDays.includes(nextDay.getDay()) && nextDay <= end) {
              nextDay.setDate(nextDay.getDate() + 1);
            }
            current = nextDay;
          } else {
            current.setDate(current.getDate() + 7);
          }
          break;
        case 'monthly':
          current.setMonth(current.getMonth() + 1);
          break;
        case 'custom':
          current.setDate(current.getDate() + (baseEvent.customRecurrence || 1));
          break;
        default:
          current = new Date(end.getTime() + 1);
      }
    }
    
    return recurringEvents;
  };

  const getAllEvents = () => {
    const allEvents = [...events];
    
    events.filter(event => event.recurrence !== 'none').forEach(event => {
      const recurring = generateRecurringEvents(event, event.date, event.endDate);
      allEvents.push(...recurring);
    });
    
    return allEvents;
  };

  const filteredEvents = getAllEvents().filter(event => {
    const matchesSearch = event.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         event.description.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = filterCategory === 'all' || event.category === filterCategory;
    return matchesSearch && matchesCategory;
  });

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();
    
    const days = [];
    
    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      days.push(null);
    }
    
    // Add all days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      days.push(new Date(year, month, day));
    }
    
    return days;
  };

  const formatDate = (date) => {
    return date.toISOString().split('T')[0];
  };

  const getEventsForDate = (date) => {
    const dateStr = formatDate(date);
    return filteredEvents.filter(event => event.date === dateStr);
  };

  const isToday = (date) => {
    const today = new Date();
    return date.getDate() === today.getDate() &&
           date.getMonth() === today.getMonth() &&
           date.getFullYear() === today.getFullYear();
  };

  const hasConflict = (eventId) => {
    return conflicts.some(conflict => conflict.event1 === eventId || conflict.event2 === eventId);
  };

  const resetForm = () => {
    setEventForm({
      title: '',
      date: '',
      time: '',
      description: '',
      category: 'personal',
      recurrence: 'none',
      customRecurrence: 1,
      weeklyDays: [],
      endDate: ''
    });
    setEditingEvent(null);
  };

const handleDateClick = (date) => {
  const today = new Date();
  if (date < new Date(today.setHours(0, 0, 0, 0))) {
    alert("Past date selection not allowed!");
    return;
  }
  setSelectedDate(date);
  setShowForm(true);
};


  const handleEventClick = (event, e) => {
    e.stopPropagation();
    setEditingEvent(event);
    setEventForm({
      title: event.title,
      date: event.date,
      time: event.time,
      description: event.description,
      category: event.category,
      recurrence: event.recurrence || 'none',
      customRecurrence: event.customRecurrence || 1,
      weeklyDays: event.weeklyDays || [],
      endDate: event.endDate || ''
    });
    setShowEventForm(true);
  };

  const handleFormSubmit = () => {
    if (!eventForm.title || !eventForm.date || !eventForm.time) {
      alert('Please fill in all required fields');
      return;
    }

    const eventData = {
      id: editingEvent ? editingEvent.id : Date.now().toString(),
      title: eventForm.title,
      date: eventForm.date,
      time: eventForm.time,
      description: eventForm.description,
      category: eventForm.category,
      recurrence: eventForm.recurrence,
      customRecurrence: eventForm.customRecurrence,
      weeklyDays: eventForm.weeklyDays,
      endDate: eventForm.endDate
    };

    if (editingEvent) {
      setEvents(prev => prev.map(event => 
        event.id === editingEvent.id ? eventData : event
      ));
    } else {
      setEvents(prev => [...prev, eventData]);
    }

    setShowEventForm(false);
    resetForm();
  };

  const handleDeleteEvent = (eventId) => {
    if (window.confirm('Are you sure you want to delete this event?')) {
      setEvents(prev => prev.filter(event => event.id !== eventId));
      setShowEventForm(false);
      resetForm();
    }
  };

  const handleDragStart = (e, event) => {
    setDraggedEvent(event);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e, targetDate) => {
    e.preventDefault();
    if (draggedEvent && targetDate) {
      const newDate = formatDate(targetDate);
      if (draggedEvent.isRecurring) {
        // For recurring events, create a new standalone event
        const newEvent = {
          ...draggedEvent,
          id: Date.now().toString(),
          date: newDate,
          isRecurring: false,
          parentId: undefined
        };
        setEvents(prev => [...prev, newEvent]);
      } else {
        // For regular events, update the date
        setEvents(prev => prev.map(event => 
          event.id === draggedEvent.id 
            ? { ...event, date: newDate }
            : event
        ));
      }
    }
    setDraggedEvent(null);
  };

  const navigateMonth = (direction) => {
    setCurrentDate(prev => {
      const newDate = new Date(prev);
      newDate.setMonth(prev.getMonth() + direction);
      return newDate;
    });
  };

  const handleWeeklyDayToggle = (dayIndex) => {
    setEventForm(prev => ({
      ...prev,
      weeklyDays: prev.weeklyDays.includes(dayIndex)
        ? prev.weeklyDays.filter(day => day !== dayIndex)
        : [...prev.weeklyDays, dayIndex]
    }));
  };

  const days = getDaysInMonth(currentDate);
  const monthYear = currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });

  return (
    <div className="max-w-6xl mx-auto p-6 bg-gray-50 min-h-screen">
      <div className="bg-white rounded-lg shadow-lg p-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-4">
            <Calendar className="w-8 h-8 text-blue-600" />
            <h1 className="text-3xl font-bold text-gray-800">Event Calendar</h1>
          </div>
          
          {/* Search and Filter */}
          <div className="flex items-center gap-4">
            <div className="relative">
              <Search className="w-4 h-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
              <input
                type="text"
                placeholder="Search events..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="all">All Categories</option>
              {categories.map(cat => (
                <option key={cat.value} value={cat.value}>{cat.label}</option>
              ))}
            </select>
          </div>
        </div>

        {/* Month Navigation */}
        <div className="flex items-center justify-between mb-6">
          <button
            onClick={() => navigateMonth(-1)}
            className="flex items-center gap-2 px-4 py-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors"
          >
            <ChevronLeft className="w-4 h-4" />
            Previous
          </button>
          
          <h2 className="text-2xl font-semibold text-gray-800">{monthYear}</h2>
          
          <button
            onClick={() => navigateMonth(1)}
            className="flex items-center gap-2 px-4 py-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors"
          >
            Next
            <ChevronRight className="w-4 h-4" />
          </button>
        </div>

        {/* Conflicts Warning */}
        {conflicts.length > 0 && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg flex items-center gap-2">
            <AlertCircle className="w-5 h-5 text-red-600" />
            <span className="text-red-700">
              {conflicts.length} event conflict{conflicts.length > 1 ? 's' : ''} detected. Check events with red borders.
            </span>
          </div>
        )}

        {/* Calendar Grid */}
        <div className="grid grid-cols-7 gap-1 mb-6">
          {/* Day Headers */}
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
            <div key={day} className="p-4 text-center font-semibold text-gray-600 bg-gray-100">
              {day}
            </div>
          ))}
          
          {/* Calendar Days */}
          {days.map((day, index) => (
            <div
              key={index}
              className={`min-h-32 p-2 border border-gray-200 cursor-pointer transition-colors ${
                day ? 'hover:bg-blue-50' : 'bg-gray-50'
              } ${day && isToday(day) ? 'bg-blue-100 border-blue-300' : ''}`}
              onClick={() => day && handleDateClick(day)}
              onDragOver={handleDragOver}
              onDrop={(e) => day && handleDrop(e, day)}
            >
              {day && (
                <>
                  <div className={`text-sm font-medium mb-1 ${isToday(day) ? 'text-blue-700' : 'text-gray-700'}`}>
                    {day.getDate()}
                  </div>
                  <div className="space-y-1">
                    {getEventsForDate(day).slice(0, 3).map(event => {
                      const category = categories.find(cat => cat.value === event.category);
                      return (
                        <div
                          key={event.id}
                          className={`text-xs p-1 rounded cursor-pointer transition-all ${category?.color || 'bg-gray-500'} text-white hover:shadow-md ${
                            hasConflict(event.id) ? 'ring-2 ring-red-500' : ''
                          }`}
                          draggable={!event.isRecurring}
                          onDragStart={(e) => handleDragStart(e, event)}
                          onClick={(e) => handleEventClick(event, e)}
                          title={`${event.title} at ${event.time}${hasConflict(event.id) ? ' (CONFLICT)' : ''}`}
                        >
                          <div className="flex items-center gap-1">
                            <Clock className="w-3 h-3" />
                            <span className="truncate">{event.title}</span>
                          </div>
                        </div>
                      );
                    })}
                    {getEventsForDate(day).length > 3 && (
                      <div className="text-xs text-gray-500 text-center">
                        +{getEventsForDate(day).length - 3} more
                      </div>
                    )}
                  </div>
                </>
              )}
            </div>
          ))}
        </div>

        {/* Add Event Button */}
        <button
          onClick={() => setShowEventForm(true)}
          className="fixed bottom-6 right-6 bg-blue-600 text-white p-4 rounded-full shadow-lg hover:bg-blue-700 transition-colors"
        >
          <Plus className="w-6 h-6" />
        </button>

        {/* Event Form Modal */}
        {showEventForm && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg p-6 w-full max-w-md max-h-90vh overflow-y-auto">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-semibold">
                  {editingEvent ? 'Edit Event' : 'Add Event'}
                </h3>
                <button
                  onClick={() => {
                    setShowEventForm(false);
                    resetForm();
                  }}
                  className="text-gray-500 hover:text-gray-700"
                >
                  <X className="w-6 h-6" />
                </button>
              </div>

              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Event Title *
                  </label>
                  <input
                    type="text"
                    value={eventForm.title}
                    onChange={(e) => setEventForm(prev => ({ ...prev, title: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    required
                  />
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Date *
                    </label>
                    <input
                      type="date"
                      value={eventForm.date}
                      onChange={(e) => setEventForm(prev => ({ ...prev, date: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Time *
                    </label>
                    <input
                      type="time"
                      value={eventForm.time}
                      onChange={(e) => setEventForm(prev => ({ ...prev, time: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      required
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    value={eventForm.description}
                    onChange={(e) => setEventForm(prev => ({ ...prev, description: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    rows={3}
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Category
                  </label>
                  <select
                    value={eventForm.category}
                    onChange={(e) => setEventForm(prev => ({ ...prev, category: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    {categories.map(cat => (
                      <option key={cat.value} value={cat.value}>{cat.label}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Recurrence
                  </label>
                  <select
                    value={eventForm.recurrence}
                    onChange={(e) => setEventForm(prev => ({ ...prev, recurrence: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  >
                    <option value="none">No Recurrence</option>
                    <option value="daily">Daily</option>
                    <option value="weekly">Weekly</option>
                    <option value="monthly">Monthly</option>
                    <option value="custom">Custom</option>
                  </select>
                </div>

                {eventForm.recurrence === 'weekly' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Repeat on Days
                    </label>
                    <div className="flex gap-2 flex-wrap">
                      {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day, index) => (
                        <button
                          key={day}
                          type="button"
                          onClick={() => handleWeeklyDayToggle(index)}
                          className={`px-3 py-1 text-sm border rounded ${
                            eventForm.weeklyDays.includes(index)
                              ? 'bg-blue-600 text-white border-blue-600'
                              : 'bg-white text-gray-700 border-gray-300'
                          }`}
                        >
                          {day}
                        </button>
                      ))}
                    </div>
                  </div>
                )}

                {eventForm.recurrence === 'custom' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Repeat every (days):
                    </label>
                    <input
                      type="number"
                      min="1"
                      value={eventForm.customRecurrence}
                      onChange={(e) => setEventForm(prev => ({ ...prev, customRecurrence: parseInt(e.target.value) || 1 }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                )}

                {eventForm.recurrence !== 'none' && (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      End Date (optional)
                    </label>
                    <input
                      type="date"
                      value={eventForm.endDate}
                      onChange={(e) => setEventForm(prev => ({ ...prev, endDate: e.target.value }))}
                      className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                  </div>
                )}

                <div className="flex gap-2 pt-4">
                  <button
                    type="button"
                    onClick={handleFormSubmit}
                    className="flex-1 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition-colors flex items-center justify-center gap-2"
                  >
                    <Save className="w-4 h-4" />
                    {editingEvent ? 'Update' : 'Save'}
                  </button>
                  {editingEvent && !editingEvent.isRecurring && (
                    <button
                      type="button"
                      onClick={() => handleDeleteEvent(editingEvent.id)}
                      className="bg-red-600 text-white py-2 px-4 rounded-lg hover:bg-red-700 transition-colors flex items-center justify-center gap-2"
                    >
                      <Trash2 className="w-4 h-4" />
                      Delete
                    </button>
                  )}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventCalendar;
